#Process the recorded transmition (with noise)
print("Signal Received: "+str(signal))


#sweep signal with header array to find position that starts the transmition
header_size = len(header)
print("header size: " + str(header_size))
signal_size = len(signal)
print("signal size: " + str(signal_size))

check=0
start_pos=0
best_check=header_size

for i in range(0, (signal_size - (header_size-1)), 1):  #MaxI is the number of times the header fits in the signal in one sweep
    
    for j in range(i, header_size, 1):  #Store in Var check the "distance" between the header and current position in Var signal. Will slice at MinDist position
        #print(j)
        check = check + (abs(signal[j]-header[j])^2)
        print(check)
        
    if check < best_check:
        print("New best starting position found")
        best_check = check
        start_pos = i   #starting position for array slice (removing header)
        print("New Min Dist: " + str(check))
        print("New Optimal Starting Position: " + str(start_pos))
    
    check=0

print("Start Position Found: " + str(start_pos))
print("Slicing Received Message")
message = signal[(start_pos):] #Here we slice the received signal and remove the header and everything before the header
print("Message Array Sliced")
print("Message Received: " + str(message))   

app_decoder.app_decoder(message) #decode message received
